import React,{useState , useEffect} from 'react'

const Context = React.createContext()

function ContextProvider(props){
    const [allPhotos , setAllPhotos] = useState([])
    const [cartItems,setCartItems] = useState([])
    const imgUrl = 'https://raw.githubusercontent.com/bobziroll/scrimba-react-bootcamp-images/master/images.json'

    useEffect(()=>{
        fetch(imgUrl)
            .then(response=>response.json())
            .then(data=>setAllPhotos(data))
    },[])

    function toggleFavorite(id) {
        const updatedArr = allPhotos.map(photo => {
            if(photo.id===id){
                return {...photo, isFavorite:!photo.isFavorite}
            }
            return photo
        })
        setAllPhotos(updatedArr)
    }

    function addToCart(newItem) {
        const checkItem = cartItems.find(item => item.id===newItem.id)
        if(checkItem) {
            setCartItems(prevItems=>prevItems.filter(item => item.id===newItem.id))
        } else {
            setCartItems(prevItems => [...prevItems,newItem])
        }

    }

    return(
        <Context.Provider value={{allPhotos,toggleFavorite,cartItems,addToCart}}>
            {props.children}
        </Context.Provider>
    )

}

export {ContextProvider , Context}
